            box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
            display: none;
        }

        #modal img {
            max-width: 100%;
            max-height: 100vh;
            object-fit: contain;
            margin: 0 auto;
        }

        #backdrop {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100vh;
            background-color: rgba(0, 0, 0, 0.5);
            display: none;
        }
    </style>
</head>
