        <img src="https://picsum.photos/id/60/200/140" data-large="https://picsum.photos/id/60/1200/800" alt="Image 6">
    </div>
    <div id="backdrop"></div>
    <div id="modal" role="dialog" aria-modal="true">
        <button id="close-btn" onclick="closeModal()">Close</button>
        <img id="modal-image" src="" alt="Selected Image">
    </div>

    <script>
        function openModal(event) {
            const backdrop = document.getElementById('backdrop');
            const modal = document.getElementById('modal');
            const image = event.target;
            const largeImage = image.dataset.large;
            backdrop.style.display = 'block';
            modal.style.display = 'block';
            document.getElementById('modal-image').src = largeImage;
        }

        function closeModal() {
            const backdrop = document.getElementById('backdrop');
            const modal = document.getElementById('modal');
            backdrop.style.display = 'none';
            modal.style.display = 'none';
        }

        const gallery = document.getElementById('gallery');
        gallery.addEventListener('click', (event) => {
            if (event.target.tagName === 'IMG') {
                openModal(event);
            }
        });

        window.addEventListener('keydown', (event) => {
            if (event.key === 'Escape') {
                closeModal();
            }
        });

        document.getElementById('backdrop').addEventListener('click', closeModal);
        document.getElementById('close-btn').addEventListener('click', closeModal);
    </script>
