<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Star Wars Audio Player</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            text-align: center;
        }
        #audio-container {
            width: 300px;
            height: 200px;
            border: 1px solid black;
            margin: 20px auto;
            display: inline-block;
        }
        #audio-container audio {
            width: 100%;
            height: 100%;
            border: none;
            margin: 0;
            padding: 0;
        }
        #volume {
            width: 100%;
            height: 20px;
            padding: 0;
            margin: 0;
            border: none;
            border-radius: 5px;
            background-color: #ccc;
            font-size: 16px;
            font-weight: bold;
            cursor: pointer;
        }
        #volume input[type="range"] {
            width: 100%;
            height: 20px;
            padding: 0;
            margin: 0;
            border: none;
            border-radius: 5px;
            background-color: #ccc;
            font-size: 16px;
            font-weight: bold;
            cursor: pointer;
        }
    </style>
</head>
<body>
    <div id="audio-container">
        <audio id="audio" controls>
            <source src="https://www2.cs.uic.edu/~i101/SoundFiles/StarWars60.wav" type="audio/wav">
            Your browser does not support the audio element.
        </audio>
        <input type="range" id="volume" min="0" max="100" value="50">
        <label for="volume">Volume: <span id="volume-value">50</span></label>
        <button id="play-pause">Play</button>
        <button id="pause-button">Pause</button>
    </div>

    <script>
        const audio = document.getElementById('audio');
        const volume = document.getElementById('volume');
        const volumeValue = document.getElementById('volume-value');
        const playPauseButton = document.getElementById('play-pause');
        const pauseButton = document.getElementById('pause-button');

        playPauseButton.addEventListener('click', () => {
            if (audio.paused) {
                audio.play();
                playPauseButton.textContent = 'Pause';
            } else {
                audio.pause();
                playPauseButton.textContent = 'Play';
            }
        });

        volume.addEventListener('input', () => {
            const volumeValue = parseInt(volumeValue.value);
            audio.volume = volumeValue / 100;
            volumeValue.textContent = volumeValue.value;
        });

        audio.addEventListener('timeupdate', () => {
            const currentTime = audio.currentTime;
            const duration = audio.duration;
            const progress = (currentTime / duration) * 100;
            volumeValue.textContent = `${progress.toFixed(2)}%`;
        });
    </script>
</body>
</html>
