            background-color: #3e8e41;
        }

        .volume {
            width: 100%;
            height: 20px;
            margin: 10px;
            border: 1px solid #ccc;
            border-radius: 10px;
            background-color: #f0f0f0;
        }

        .volume input[type="range"] {
            width: 100%;
            height: 100%;
            border: none;
            border-radius: 10px;
        }
    </style>
</head>
<body>
    <h1>Custom Audio Player</h1>
    <div class="controls">
        <button id="play-pause-button">Play</button>
        <button id="stop-button" style="display: none;">Stop</button>
        <div class="volume">
            <input type="range" id="volume-slider" min="0" max="100" value="50">
            <label for="volume-slider">Volume (0-100)</label>
        </div>
    </div>
    <audio id="audio-player" src="https://www2.cs.uic.edu/~i101/SoundFiles/StarWars60.wav" loop="loop" preload="auto"></audio>

    <script>
        // Get the audio element and the controls
        const audioPlayer = document.getElementById('audio-player');
        const playPauseButton = document.getElementById('play-pause-button');
        const stopButton = document.getElementById('stop-button');
        const volumeSlider = document.getElementById('volume-slider');

        // Add event listeners
        playPauseButton.addEventListener('click', () => {
            if (audioPlayer.paused) {
                audioPlayer.play();
                playPauseButton.textContent = 'Pause';
            } else {
                audioPlayer.pause();
                playPauseButton.textContent = 'Play';
            }
        });

        stopButton.addEventListener('click', () => {
            audioPlayer.pause();
            audioPlayer.currentTime = 0;
            playPauseButton.textContent = 'Play';
            stopButton.style.display = 'none';
        });

        volumeSlider.addEventListener('input', () => {
            audioPlayer.volume = volumeSlider.value / 100;
        });

        // Initialize the audio player
        audioPlayer.addEventListener('canplay', () => {
            playPauseButton.style.display = 'block';
            stopButton.style.display = 'block';
        });
    </script>
</body>
</html>
