  <label for="comments">Comments:</label>
  <textarea id="comments" name="comments" required></textarea>

  <button onclick="submitFeedback()">Submit</button>

  <script>
    let selectedStar = null;

    function setRating(star) {
      selectedStar = star;
      // Highlight current star and remove highlight from others
      for (let i = 0; i < star.parentNode.children.length; i++) {
        star.parentNode.children[i].classList.remove('selected');
      }
      star.classList.add('selected');
    }

    function submitFeedback() {
      const rating = selectedStar ? selectedStar.dataset.value : null;
      const comment = document.getElementById('comments').value;

      alert(`Rating: ${rating}\nComment: ${comment}`);
    }
  </script>

</body>
