      <button class="answer" id="A">A) Elephant</button>
      <button class="answer" id="B">B) Rhinoceros</button>
      <button class="answer" id="C">C) Hippopotamus</button>
      <button class="answer" id="D">D) Giraffe</button>
    </div>
  </div>
  <div class="score">
    <p id="score">Score: 0/3</p>
  </div>
  <button id="submit">Submit</button>
  <button id="try-again">Try Again</button>

  <script>
    let score = 0;
    let questions = [
      {
        question: "Which animal is the largest land animal?",
        options: ["A) Elephant", "B) Rhinoceros", "C) Hippopotamus", "D) Giraffe"],
        correct: "A) Elephant"
      },
      {
        question: "Which animal is known for its black-and-white stripes?",
        options: ["A) Zebra", "B) Tiger", "C) Panda", "D) Skunk"],
        correct: "A) Zebra"
      },
      {
        question: "Which mammal can truly fly?",
        options: ["A) Bat", "B) Flying squirrel", "C) Ostrich", "D) Eagle"],
        correct: "A) Bat"
      }
    ];

    document.getElementById("submit").addEventListener("click", function() {
      let answers = document.querySelectorAll(".answer");
      let score = 0;
      let correct = 0;
      for (let i = 0; i < questions.length; i++) {
        let option = document.getElementById(`option${i + 1}`);
        option.addEventListener("click", function() {
          if (option.value === questions[i].correct) {
            score++;
            console.log("Correct!");
            document.getElementById("score").innerHTML = `Score: ${score}/${questions.length}`;
            document.getElementById("score").style.color = "green";
          } else {
            console.log("Incorrect!");
            document.getElementById("score").innerHTML = `Score: ${score}/${questions.length}`;
            document.getElementById("score").style.color = "red";
          }
          for (let j = 0; j < answers.length; j++) {
            if (answers[j].checked) {
              correct++;
              document.getElementById(`option${j + 1}`).style.background = "green";
            } else {
              document.getElementById(`option${j + 1}`).style.background = "red";
            }
          }
        });
      }
      if (score === questions.length) {
        alert("You got all 3 questions correct!");
      }
    });

    document.getElementById("try-again").addEventListener("click", function() {
      score = 0;
      for (let i = 0; i < questions.length; i++) {
        let option = document.getElementById(`option${i + 1}`);
        option.style.background = "lightgray";
        option.checked = false;
      }
      document.getElementById("score").innerHTML = "Score: 0/3";
    });
  </script>
