<div id="quiz">
  <div class="question">1) Which animal is the largest land animal?
    <div class="answer" onclick="checkAnswer('q1', 'A')">A) Elephant</div>
    <div class="answer" onclick="checkAnswer('q1', 'B')">B) Rhinoceros</div>
    <div class="answer" onclick="checkAnswer('q1', 'C')">C) Hippopotamus</div>
    <div class="answer" onclick="checkAnswer('q1', 'D')">D) Giraffe</div>
  </div>
  <div class="question">2) Which animal is known for its black-and-white stripes?
    <div class="answer" onclick="checkAnswer('q2', 'A')">A) Zebra</div>
    <div class="answer" onclick="checkAnswer('q2', 'B')">B) Tiger</div>
    <div class="answer" onclick="checkAnswer('q2', 'C')">C) Panda</div>
    <div class="answer" onclick="checkAnswer('q2', 'D')">D) Skunk</div>
  </div>
  <div class="question">3) Which mammal can truly fly?
    <div class="answer" onclick="checkAnswer('q3', 'A')">A) Bat</div>
    <div class="answer" onclick="checkAnswer('q3', 'B')">B) Flying squirrel</div>
    <div class="answer" onclick="checkAnswer('q3', 'C')">C) Ostrich</div>
    <div class="answer" onclick="checkAnswer('q3', 'D')">D) Eagle</div>
  </div>
  <div id="result" class="result"></div>
  <button onclick="submitQuiz()">Submit</button>
  <button onclick="resetQuiz()">Try again</button>
</div>

<script>
  let score = 0;
  const answers = { 'q1': 'A', 'q2': 'A', 'q3': 'A' };

  function checkAnswer(q, ans) {
    const btns = document.querySelectorAll(`#quiz .question input[type="radio"][value="${ans}"]`);
    if (btns.length) {
      btns[0].checked = true;
    }
  }

  function submitQuiz() {
    const totalQuestions = document.querySelectorAll('.question').length;
    let correctCount = 0;
    const resultDiv = document.getElementById('result');

    for (let i = 1; i <= totalQuestions; i++) {
      const question = document.querySelector(`#quiz .question:nth-of-type(${i})`);
      const userAns = question.querySelector('input[type="radio"]:checked').value;
      const correctAns = answers[`q${i}`];
      const answerContainer = question.querySelector('.answer');
      answerContainer.className = userAns == correctAns ? 'correct' : 'incorrect';
      if (userAns == correctAns) {
        correctCount++;
      }
    }

    score = correctCount;
    resultDiv.className = 'result';
    if (score > 0) {
      resultDiv.textContent = `You got ${correctCount}/${totalQuestions} correct!`;
    } else {
      resultDiv.textContent = 'None of your answers are correct. Try again!';
    }
  }

  function resetQuiz() {
    document.getElementById('result').textContent = '';
    for (let i = 1; i <= 3; i++) {
      const question = document.querySelector(`#quiz .question:nth-of-type(${i})`);
      const radios = question.querySelectorAll('input[type="radio"]');
      radios.forEach(radio => radio.checked = false);
    }
  }
</script>
</body>
</html>
