        let score = 0;
        questions.forEach((question, index) => {
            const selectedAnswer = question.querySelector('input[type="radio"]:checked');
            if (selectedAnswer && selectedAnswer.value === correctAnswers[index]) {
                score++;
                question.classList.add('highlight-correct');
            } else {
                question.classList.add('highlight-incorrect');
            }
        });

        resultElement.textContent = `Your Score: ${score}/3`;
        resultElement.classList.remove('highlight-correct', 'highlight-incorrect');
        resultElement.classList.add('highlight-correct');
    }

    function resetQuiz() {
        questions.forEach(question => {
            question.querySelectorAll('input[type="radio"]').forEach(checkbox => checkbox.checked = false);
            question.classList.remove('highlight-correct', 'highlight-incorrect');
        });
        resultElement.textContent = '';
        resultElement.classList.remove('highlight-correct', 'highlight-incorrect');
